// Импортируем библиотеки
import React, { useState } from 'react';
import { Link } from "react-router-dom";

// Импортируем стили
import './Join.css';

// Экспортируем функцию 
export default function LogIn() {
  const [name, setName] = useState('');
  const [room, setRoom] = useState('');

  return (
    <div className="join__out--container">
      <div className="join__inside--container">
        <h1 className="heading">Join</h1>
        <div>
          <input placeholder="Name" className="join__input" type="text" onChange={(event) => setName(event.target.value)} />
        </div>
        <div>
          <input placeholder="Room" className="join__input margintop-20" type="text" onChange={(event) => setRoom(event.target.value)} />
        </div>
        <Link onClick={e => (!name || !room) ? e.preventDefault() : null} to={`/chat?name=${name}&room=${room}`}>
          <button className={'button margintop-20'} type="submit">Sign In</button>
        </Link>

      </div>
      <span > By Eugene Firman</span>
    </div>
  );
}
